class DBHelper{static get DATABASE_URL(){const port=1337;return"http://localhost:1337/"}static get DATABASE_RESTAURANTS_URL(){return`${this.DATABASE_URL}restaurants`}static get DATABASE_REVIEWS_URL(){return`${this.DATABASE_URL}reviews`}static initDB(){return navigator.serviceWorker?idb.open("mws",1,upgradeDb=>{const tableRestaurant=upgradeDb.createObjectStore("restaurants",{keyPath:"id"});tableRestaurant.createIndex("changed","changed",{unique:!1})}):(console.log("Browser can't support serviceworkers"),Promise.resolve())}static fetchRestaurants(callback){fetch(DBHelper.DATABASE_RESTAURANTS_URL).then(response=>response.json()).then(restaurants=>{this.initDB().then(db=>{if(!db)return;let tx,store=db.transaction("restaurants","readwrite").objectStore("restaurants");restaurants.forEach(function(restaurant){store.get(parseInt(restaurant.id)).then(local_restaurant=>{"false"===local_restaurant.changed&&store.put(restaurant)}).catch(error=>{store.put(restaurant)})}),callback(null,restaurants)})}).catch(error=>{console.log(error),this.initDB().then(db=>{var tx,store;db&&db.transaction("restaurants").objectStore("restaurants").getAll().then(restaurants=>{callback(null,restaurants)}).catch(error=>callback(error,null))}).catch(error=>callback(error,null))})}static fetchRestaurantById(id,callback){DBHelper.fetchRestaurants((error,restaurants)=>{if(error)callback(error,null);else{const restaurant=restaurants.find(r=>r.id==id);restaurant?callback(null,restaurant):callback("Restaurant does not exist",null)}})}static fetchRestaurantByCuisine(cuisine,callback){DBHelper.fetchRestaurants((error,restaurants)=>{if(error)callback(error,null);else{const results=restaurants.filter(r=>r.cuisine_type==cuisine);callback(null,results)}})}static fetchRestaurantByNeighborhood(neighborhood,callback){DBHelper.fetchRestaurants((error,restaurants)=>{if(error)callback(error,null);else{const results=restaurants.filter(r=>r.neighborhood==neighborhood);callback(null,results)}})}static fetchRestaurantByCuisineAndNeighborhood(cuisine,neighborhood,callback){DBHelper.fetchRestaurants((error,restaurants)=>{if(error)callback(error,null);else{let results=restaurants;"all"!=cuisine&&(results=results.filter(r=>r.cuisine_type==cuisine)),"all"!=neighborhood&&(results=results.filter(r=>r.neighborhood==neighborhood)),callback(null,results)}})}static fetchNeighborhoods(callback){DBHelper.fetchRestaurants((error,restaurants)=>{if(error)callback(error,null);else{const neighborhoods=restaurants.map((v,i)=>restaurants[i].neighborhood),uniqueNeighborhoods=neighborhoods.filter((v,i)=>neighborhoods.indexOf(v)==i);callback(null,uniqueNeighborhoods)}})}static fetchCuisines(callback){DBHelper.fetchRestaurants((error,restaurants)=>{if(error)callback(error,null);else{const cuisines=restaurants.map((v,i)=>restaurants[i].cuisine_type),uniqueCuisines=cuisines.filter((v,i)=>cuisines.indexOf(v)==i);callback(null,uniqueCuisines)}})}static urlForRestaurant(restaurant){return`./restaurant.html?id=${restaurant.id}`}static imageUrlForRestaurant(restaurant,size){return`/img/${size}-${restaurant.photograph||restaurant.id}.webp`}static mapMarkerForRestaurant(restaurant,map){const marker=new google.maps.Marker({position:restaurant.latlng,title:restaurant.name,url:DBHelper.urlForRestaurant(restaurant),map:map,animation:google.maps.Animation.DROP});return marker}static toggleFavourite(id){console.log(`Change favorite value for ${id}`),DBHelper.initDB().then(db=>{if(!db)return;let tx,store=db.transaction("restaurants","readwrite").objectStore("restaurants");store.get(parseInt(id)).then(restaurant=>{const isFavorite="true"===restaurant.is_favorite;restaurant.is_favorite=(!isFavorite).toString(),restaurant.updatedAt=Date.now(),restaurant.changed="true",store.put(restaurant);const favouriteLink=document.getElementById(`fav-link-${restaurant.id}`);favouriteLink.innerHTML=isFavorite?"♡":"♥",navigator.serviceWorker.controller.postMessage("favsync")})})}}